import unittest
import sys
import os
sys.path.append(os.getcwd())
import tp_2


class Test_Functions(unittest.TestCase):

    def test_min1(self):
        base = tp_2.get_num_base("test_file_10")
        self.assertEqual(tp_2._min(base), 76)

    def test_min2(self):
        base = tp_2.get_num_base("test_file_1000")
        self.assertEqual(tp_2._min(base), 1)

    def test_max1(self):
        base = tp_2.get_num_base("test_file_10")
        self.assertEqual(tp_2._max(base), 905)

    def test_max2(self):
        base = tp_2.get_num_base("test_file_1000")
        self.assertEqual(tp_2._max(base), 999)

    def test_sum1(self):
        base = tp_2.get_num_base("test_file_10")
        self.assertEqual(tp_2._sum(base), 4840)

    def test_sum2(self):
        base = tp_2.get_num_base("test_file_1000")
        self.assertEqual(tp_2._sum(base), 494000)

    def test_mult1(self):
        base = tp_2.get_num_base("test_file_10")
        self.assertEqual(tp_2._mult(base), 70728390420044306568998400)

    def test_mult2(self):
        base = tp_2.get_num_base("test_file_1000")
        self.assertEqual(tp_2._mult(base), )


if __name__ == "__main__":
    unittest.main()
